import React, { useContext } from 'react';
import ContextAPI from '../../Context/ContextAPI';
import './styles.css';

import DetalhesBebidas from '../../Pages/DetalhesComidas';

const Cards = () => {
  const { apiValueSearch } = useContext(ContextAPI);

  return (
    <div className="main">
      <div className="cards-comidas">
        {/* {console.log(apiValueSearch.results.meals.length, apiValueSearch)} */}
        {apiValueSearch.results.meals && apiValueSearch.results.meals.length > 1 && (
          window.location.href = 'http://localhost:3000/bebidas'
        //   apiValueSearch.results.meals.map((res) => (
        //     <div key={ res.strMeal }>
        //       <p>{res.strMeal}</p>
        //       <img src={ res.strMealThumb } alt={ res.strMeal } />
        //     </div>
        //   ))
        )}
      </div>
      <div className="cards-comidas">
        {apiValueSearch.results.drinks && (
          apiValueSearch.results.drinks.map((res) => (
            <div key={ res.strDrink }>
              <p>{res.strDrink}</p>
              <img src={ res.strDrinkThumb } alt={ res.strDrink } />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Cards;
