import React, { useState, useEffect, useContext } from 'react';
import ContextAPI from '../../Context/ContextAPI';
import { searchFoodIngredients } from '../../services/aPI';

const ResultsAPI = (ingrediente) => {
  const { searchComponent } = useContext(ContextAPI);

  const verificationAPI = async () => {
    const result = await searchFoodIngredients(ingrediente);
    console.log(result);
  };

  return searchComponent.length === 2 ? (
    () => verificationAPI()
  ) : (<p>loaging</p>);
};

export default ResultsAPI;
