import React, { useContext, useState } from 'react';
import ContextAPI from '../../Context/ContextAPI';
import './styles.css';

import { searchFoodIngredients,
  searchFoodName,
  searchFoodFirstLetter,
  searchDrinkIngredients,
  searchDrinkFirstLetter,
  searchDrinkName,
} from '../../services/aPI';

const SearchBar = () => {
  const { searchComponent, setApiValueSearch } = useContext(ContextAPI);
  const [nome, setNome] = useState('');
  const [radioButton, setRadioButton] = useState('');

  const handleChange = (target) => {
    if (target.name === 'radio-button') setRadioButton(target.value);

    if (target.name === 'text') setNome(target.value);
  };

  const apiOfIngredients = async () => {
    if (window.location.pathname === '/comidas') {
      const resultsFoods = await searchFoodIngredients(nome);

      setApiValueSearch({
        results: resultsFoods,
      });
    } else {
      const resultsDrinks = await searchDrinkIngredients(nome);

      setApiValueSearch({
        results: resultsDrinks,
      });
    }
  };

  const apiOfName = async () => {
    if (window.location.pathname === '/comidas') {
      const resultsFoods = await searchFoodName(nome);

      setApiValueSearch({
        results: resultsFoods,
      });
    } else {
      const resultsDrinks = await searchDrinkName(nome);

      setApiValueSearch({
        results: resultsDrinks,
      });
    }
  };

  const apiOfFirstLetter = async () => {
    if (nome.length > 1) {
      if (window.location.pathname === '/comidas') {
        const resultsFoods = await searchFoodFirstLetter(nome);

        setApiValueSearch({
          results: resultsFoods,
        });
      }
    } else {
      const resultsDrinks = await searchDrinkFirstLetter(nome);

      setApiValueSearch({
        results: resultsDrinks,
      });
    }
  };

  const handleChangeButton = () => {
    switch (radioButton) {
    case 'ingrediente':
      return apiOfIngredients();
    case 'nome':
      return apiOfName();
    case 'primeira-letra':
      return apiOfFirstLetter();
    default:
      return '';
    }
  };

  return searchComponent && (
    <div id="search-bar">
      {/* {console.log(window.location.pathname)} */}
      <input
        data-testid="search-input"
        name="text"
        type="text"
        onChange={
          (e) => handleChange(e.target)
        }
      />
      <br />
      <label htmlFor="ingrediente-button">
        <input
          type="radio"
          data-testid="ingredient-search-radio"
          id="ingrediente-button"
          name="radio-button"
          value="ingrediente"
          onChange={ (e) => handleChange(e.target) }
        />
        Ingrediente
      </label>
      <label htmlFor="nome-button">
        <input
          type="radio"
          data-testid="name-search-radio"
          id="nome-button"
          name="radio-button"
          value="nome"
          onChange={ (e) => handleChange(e.target) }
        />
        Nome
      </label>
      <label htmlFor="primeira-letra">
        <input
          type="radio"
          data-testid="first-letter-search-radio"
          id="primeira-letra"
          name="radio-button"
          value="primeira-letra"
          onChange={ (e) => handleChange(e.target) }
        />
        Primeira Letra
      </label>
      <br />
      <button
        id="primeira-letra"
        type="button"
        onClick={ handleChangeButton }
        data-testid="exec-search-btn"
      >
        Buscar
      </button>
    </div>
  );
};

export default SearchBar;
