import React, { useState, useEffect, useContext } from 'react';
import ContextAPI from '../../Context/ContextAPI';
import { searchFoodIngredients } from '../../services/aPI';

const ResultsAPI = (ingreiente) => {
  const { searchComponent } = useContext(ContextAPI);

  const verificationAPI = async () => {
    const result = await searchFoodIngredients(ingreiente);
    console.log(result);
  };

  // return searchComponent.length === 2 ? (
