import React, { useContext } from 'react';
import ContextAPI from '../../Context/ContextAPI';
import './styles.css';

import DetalhesBebidas from '../../Pages/DetalhesComidas';

const Cards = () => {
  const { apiValueSearch } = useContext(ContextAPI);

  const redirectDetailsComidas = () => {
    window.location.href = 'http://localhost:3000/bebidas';
    // alert('kkkkk');
  };

  const redirectDetailsBebidas = () => {
    window.location.href = 'http://localhost:3000/comidas';
    // alert('kkkkk');
  };

  return (
    <div className="main">
      <div className="main-cards">
        {console.log(apiValueSearch.results)}
        {apiValueSearch.results.meals.length === 0 ? <div />
          : apiValueSearch.results.meals && apiValueSearch.results.meals.length > 1 ? (
            apiValueSearch.results.meals.map((res) => (
              <div className="cards" key={ res.strMeal }>
                <p>{res.strMeal}</p>
                <img src={ res.strMealThumb } alt={ res.strMeal } />
              </div>
            ))
          ) : redirectDetailsComidas()}
      </div>
      <div>
        {apiValueSearch.results.showcase.length === 0 ? <div />
          : apiValueSearch.results.showcase && apiValueSearch.results.showcase.length > 1 ? (
            apiValueSearch.results.showcase.map((res) => (
              <div className="cards" key={ res.strDrink }>
                <p>{res.strDrink}</p>
                <img src={ res.strDrinkThumb } alt={ res.strDrink } />
              </div>
            ))
          ) : redirectDetailsBebidas()}
      </div>
    </div>
  );
};

export default Cards;
